hooks.hook:

//Language files!
if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php'))
	include $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
else
	include $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';

//Lets prepare our query
$query = array(
	'SELECT'	=> 'p.id AS pid, t.id AS tid, t.subject, t.poster, t.first_post_id, p.posted',
	'FROM'		=> 'posts AS p',
	'JOINS'		=> array(
		array(
			'LEFT JOIN'		=> 'topics AS t',
			'ON'			=> 't.id = p.topic_id'
		),
		array(
			'INNER JOIN'	=> 'forums AS f',
			'ON'			=> 'f.id=t.forum_id'
		),
		array(
			'LEFT JOIN'		=> 'forum_perms AS fp',
			'ON'			=> '(fp.forum_id=f.id AND fp.group_id='.$forum_user['g_id'].')'
		)
	),
	'WHERE'		=> '(fp.read_forum IS NULL OR fp.read_forum=1) AND p.poster_id='.$id.'',
	'ORDER BY'	=> 'p.posted DESC',
	'LIMIT'		=> '5'
);
//Hi database, got my posts?
$user_posts_result = $forum_db->query_build($query) or error(__FILE__, __LINE__);

//Create our array (not really necessary, I think)
$user_posts = array();

//Prepare the HTML for output
while($cur_post = $forum_db->fetch_assoc($user_posts_result))
	$user_posts[] = '<li'.(empty($user_posts) ? ' class="first-item"' : '') . '>' . (($cur_post['pid'] != $cur_post['first_post_id']) ? $lang_latest_posts['Reply'] : $lang_latest_posts['New']) . ' <a href="'.forum_link($forum_url['post'], $cur_post['pid']).'">' . forum_htmlencode($cur_post['subject']) . '</a> ' . format_time($cur_post['posted']) . '</li>';

//Make sure we actually got posts before trying to show them
if(!empty($user_posts)) :
?>
			<div class="ct-set data-set set<?php echo ++$forum_page['item_count'] ?>">
				<div class="ct-box data-box">
					<h3 class="ct-legend hn"><span><?php echo $lang_latest_posts['Latest posts'] ?></span></h3>
					<ul class="data-box">
						<?php echo implode("\n\t\t\t\t\t\t", $user_posts) ?>
					</ul>
				</div>
			</div>
<?php
endif;
